import React from 'react';
import styled from '@emotion/styled';
import { motion, AnimatePresence } from 'framer-motion';
import { FaTimes } from 'react-icons/fa';
import { useTheme } from '../../context/ThemeContext';

const ModalOverlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: ${props => props.isDarkMode ? 
    'rgba(0, 0, 0, 0.8)' : 
    'rgba(0, 0, 0, 0.7)'
  };
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 20px;
  backdrop-filter: blur(5px);
`;

const ModalContent = styled(motion.div)`
  background: var(--card-background);
  border-radius: 15px;
  max-width: 800px;
  width: 100%;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
  padding: 2rem;
  box-shadow: ${props => props.isDarkMode ? 
    '0 10px 30px rgba(0, 0, 0, 0.3)' : 
    '0 10px 30px rgba(0, 0, 0, 0.2)'
  };
  border: 1px solid var(--border-color);
  color: var(--text-color);

  /* Scrollbar styling */
  scrollbar-width: thin;
  scrollbar-color: ${props => props.isDarkMode ? 
    'var(--primary-color) rgba(45, 52, 54, 0.5)' : 
    'var(--primary-color) rgba(0, 0, 0, 0.1)'
  };

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: ${props => props.isDarkMode ? 
      'rgba(45, 52, 54, 0.5)' : 
      'rgba(0, 0, 0, 0.1)'
    };
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb {
    background-color: var(--primary-color);
    border-radius: 4px;

    &:hover {
      background-color: var(--hover-color);
    }
  }
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: ${props => props.isDarkMode ? 
    'rgba(45, 52, 54, 0.8)' : 
    'rgba(255, 255, 255, 0.8)'
  };
  border: none;
  font-size: 1.5rem;
  color: var(--text-color);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  transition: all 0.3s ease;
  backdrop-filter: blur(5px);

  &:hover {
    background: ${props => props.isDarkMode ? 
      'rgba(45, 52, 54, 0.95)' : 
      'rgba(255, 255, 255, 0.95)'
    };
    color: var(--primary-color);
    transform: scale(1.1);
  }
`;

const ModalHeader = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 2rem;
  padding-right: 2rem;
`;

const ModalLogo = styled.div`
  width: 80px;
  height: 80px;
  margin-right: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${props => props.isDarkMode ? 
    'rgba(45, 52, 54, 0.5)' : 
    'rgba(255, 255, 255, 0.5)'
  };
  border-radius: 10px;
  padding: 0.5rem;

  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

const ModalTitle = styled.h2`
  font-size: 2rem;
  color: var(--text-color);
  margin: 0;
  font-weight: 600;
`;

const ModalBody = styled.div`
  margin-bottom: 2rem;
`;

const ModalText = styled.p`
  font-size: 1.1rem;
  line-height: 1.8;
  color: var(--light-text);
  margin-bottom: 2rem;
  opacity: ${props => props.isDarkMode ? '0.9' : '1'};
`;

const ModalFeatures = styled.div`
  background: ${props => props.isDarkMode ? 
    'rgba(45, 52, 54, 0.5)' : 
    'rgba(0, 0, 0, 0.03)'
  };
  padding: 1.5rem;
  border-radius: 10px;
  margin-bottom: 2rem;

  h3 {
    color: var(--text-color);
    margin-bottom: 1rem;
    font-size: 1.3rem;
  }
`;

const FeatureList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const FeatureItem = styled.li`
  display: flex;
  align-items: flex-start;
  margin-bottom: 0.75rem;
  font-size: 1.1rem;
  color: var(--light-text);
  opacity: ${props => props.isDarkMode ? '0.9' : '1'};

  &:before {
    content: "•";
    color: var(--primary-color);
    font-weight: bold;
    margin-right: 0.5rem;
  }

  &:last-child {
    margin-bottom: 0;
  }
`;

const ModalFooter = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-top: 2rem;
  padding-top: 1rem;
  border-top: 1px solid var(--border-color);
`;

const ModalButton = styled.a`
  display: inline-block;
  padding: 0.75rem 1.5rem;
  background: var(--primary-color);
  color: white;
  border-radius: 5px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;

  &:hover {
    background: var(--hover-color);
    transform: translateY(-2px);
  }
`;

const AppIcon = styled.div`
  font-size: 2.5rem;
  color: var(--primary-color);
  margin-bottom: 1rem;
  display: ${props => props.hide ? 'none' : 'block'};
`;

const AppModal = ({ isOpen, app, onClose }) => {
  const { isDarkMode } = useTheme();

  if (!app) return null;

  return (
    <AnimatePresence>
      {isOpen && (
        <ModalOverlay
          isDarkMode={isDarkMode}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={onClose}
        >
          <ModalContent
            isDarkMode={isDarkMode}
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            transition={{ type: "spring", damping: 25, stiffness: 300 }}
            onClick={(e) => e.stopPropagation()}
          >
            <CloseButton onClick={onClose} isDarkMode={isDarkMode}>
              <FaTimes />
            </CloseButton>

            <ModalHeader>
              <ModalLogo isDarkMode={isDarkMode}>
                <img 
                  src={app.logo} 
                  alt={`${app.title} Logo`} 
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.style.display = 'none';
                    e.target.parentElement.querySelector('.fallback-icon').style.display = 'block';
                  }} 
                />
                <div className="fallback-icon" style={{ display: app.logo === '/images/logos/coming-soon-logo.png' ? 'block' : 'none' }}>
                  {app.icon}
                </div>
              </ModalLogo>
              <ModalTitle>{app.title}</ModalTitle>
            </ModalHeader>

            <ModalBody>
              <ModalText isDarkMode={isDarkMode}>{app.longDescription}</ModalText>

              <ModalFeatures isDarkMode={isDarkMode}>
                <h3>Key Features</h3>
                <FeatureList>
                  {app.features.map((feature, index) => (
                    <FeatureItem key={index} isDarkMode={isDarkMode}>{feature}</FeatureItem>
                  ))}
                </FeatureList>
              </ModalFeatures>
            </ModalBody>

            <ModalFooter>
              {!app.url && (
                <ModalButton href="#contact">
                  Get Notified
                </ModalButton>
              )}
            </ModalFooter>
          </ModalContent>
        </ModalOverlay>
      )}
    </AnimatePresence>
  );
};

export default AppModal;
