import React from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { FaRobot, FaCode, FaMobile, FaCloud, FaShoppingBag, FaEnvelope, FaPhone, FaMapMarkerAlt } from 'react-icons/fa';
import './App.css';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Header = styled.header`
  padding: 20px 0;
  position: fixed;
  width: 100%;
  top: 0;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(5px);
  z-index: 1000;
  border-bottom-left-radius: 15px;
  border-bottom-right-radius: 15px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
`;

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Logo = styled.h1`
  font-size: 1.5rem;
  color: #2d3436;
  margin: 0;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled.a`
  color: #2d3436;
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s ease;

  &:hover {
    color: #0984e3;
  }
`;

const Hero = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: 100px 0;
  background: linear-gradient(135deg, var(--secondary-color) 0%, var(--primary-color) 100%);
  color: white;
`;

const HeroContent = styled.div`
  max-width: 600px;
`;

const Title = styled(motion.h1)`
  font-size: 3.5rem;
  margin-bottom: 1rem;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 1.25rem;
  margin-bottom: 2rem;
  opacity: 0.9;
`;

const Button = styled(motion.a)`
  display: inline-block;
  padding: 1rem 2rem;
  background: white;
  color: var(--primary-color);
  text-decoration: none;
  border-radius: 5px;
  font-weight: 600;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-2px);
  }
`;

const Apps = styled.section`
  padding: 100px 0;
  background: var(--light-background);
`;

const SectionTitle = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 3rem;
  color: var(--text-color);
`;

const AppGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  padding: 0 20px;
`;

const AppCard = styled(motion.div)`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  cursor: pointer;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
  }
`;

const AppIcon = styled.div`
  font-size: 2.5rem;
  color: var(--primary-color);
  margin-bottom: 1rem;
`;

const AppTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: var(--text-color);
`;

const AppDescription = styled.p`
  color: var(--light-text);
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const AppLink = styled.a`
  display: inline-block;
  padding: 0.5rem 1rem;
  background: var(--primary-color);
  color: white;
  border-radius: 5px;
  font-weight: 500;
  transition: background 0.3s ease;

  &:hover {
    background: var(--secondary-color);
  }
`;

const About = styled.section`
  padding: 100px 0;
  background: white;
`;

const AboutContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 2rem;
  }
`;

const AboutText = styled.div`
  h2 {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    color: var(--text-color);
  }

  p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: var(--light-text);
    margin-bottom: 1.5rem;
  }
`;

const AboutImage = styled.div`
  img {
    width: 100%;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  }
`;

const Contact = styled.section`
  padding: 100px 0;
  background: var(--light-background);
`;

const ContactContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 2rem;
  }
`;

const ContactInfo = styled.div`
  h2 {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    color: var(--text-color);
  }

  p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: var(--light-text);
    margin-bottom: 2rem;
  }
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 1.5rem;

  svg {
    font-size: 1.5rem;
    color: var(--primary-color);
    margin-right: 1rem;
  }

  span {
    font-size: 1.1rem;
    color: var(--text-color);
  }
`;

const ContactForm = styled.form`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  font-weight: 500;
  color: var(--text-color);
`;

const Input = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #ddd;
  border-radius: 5px;
  font-size: 1rem;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: var(--primary-color);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #ddd;
  border-radius: 5px;
  font-size: 1rem;
  min-height: 150px;
  resize: vertical;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: var(--primary-color);
  }
`;

const SubmitButton = styled.button`
  background: var(--primary-color);
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: background 0.3s ease;

  &:hover {
    background: var(--secondary-color);
  }
`;

function App() {
  const scrollToSection = (e, sectionId) => {
    e.preventDefault();
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80, // Offset for the fixed header
        behavior: 'smooth'
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    alert('Thank you for your message! We will get back to you soon.');
  };

  return (
    <div className="App">
      <Header>
        <Nav>
          <Logo>Seagull Technologies</Logo>
          <NavLinks>
            <NavLink href="#apps" onClick={(e) => scrollToSection(e, 'apps')}>Our Apps</NavLink>
            <NavLink href="#about" onClick={(e) => scrollToSection(e, 'about')}>About</NavLink>
            <NavLink href="#contact" onClick={(e) => scrollToSection(e, 'contact')}>Contact</NavLink>
          </NavLinks>
        </Nav>
      </Header>

      <Hero>
        <Container>
          <HeroContent>
            <Title
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              We Build AI Apps
            </Title>
            <Subtitle
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              We specialize in developing consumer-facing AI mobile applications with enhanced user experience.
            </Subtitle>
            <Button
              href="#apps"
              onClick={(e) => scrollToSection(e, 'apps')}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Get Started
            </Button>
          </HeroContent>
        </Container>
      </Hero>

      <Apps id="apps">
        <Container>
          <SectionTitle>Our AI Apps</SectionTitle>
          <AppGrid>
            <AppCard
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              viewport={{ once: true }}
              onClick={() => window.open('https://dresscode.ai', '_blank')}
            >
              <AppIcon>
                <FaShoppingBag />
              </AppIcon>
              <AppTitle>Dresscode AI</AppTitle>
              <AppDescription>
                An AI-powered fashion recommendation app that helps users discover their perfect style based on personal preferences and body type.
              </AppDescription>
              <AppLink href="https://dresscode.ai" target="_blank" rel="noopener noreferrer">
                Learn More
              </AppLink>
            </AppCard>

            <AppCard
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
              viewport={{ once: true }}
              style={{ opacity: 0.7 }}
            >
              <AppIcon>
                <FaRobot />
              </AppIcon>
              <AppTitle>Coming Soon</AppTitle>
              <AppDescription>
                We're working on exciting new AI applications. Stay tuned for more innovative solutions from Seagull Technologies.
              </AppDescription>
              <AppLink href="#contact">
                Get Notified
              </AppLink>
            </AppCard>

            <AppCard
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.4 }}
              viewport={{ once: true }}
              style={{ opacity: 0.7 }}
            >
              <AppIcon>
                <FaCloud />
              </AppIcon>
              <AppTitle>Coming Soon</AppTitle>
              <AppDescription>
                We're working on exciting new AI applications. Stay tuned for more innovative solutions from Seagull Technologies.
              </AppDescription>
              <AppLink href="#contact">
                Get Notified
              </AppLink>
            </AppCard>
          </AppGrid>
        </Container>
      </Apps>

      <About id="about">
        <Container>
          <AboutContent>
            <AboutText>
              <motion.h2
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                viewport={{ once: true }}
              >
                About Seagull Technologies
              </motion.h2>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.2 }}
                viewport={{ once: true }}
              >
                Seagull Technologies is a forward-thinking AI app development agency founded with a vision to create innovative mobile applications that leverage cutting-edge artificial intelligence to solve real-world problems.
              </motion.p>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.3 }}
                viewport={{ once: true }}
              >
                Our team of experienced developers, designers, and AI specialists work together to build user-friendly applications that deliver exceptional value to our clients and end-users.
              </motion.p>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.4 }}
                viewport={{ once: true }}
              >
                We believe in the power of AI to transform industries and enhance human experiences. Our mission is to make AI accessible and practical through beautifully designed mobile applications.
              </motion.p>
            </AboutText>
            <AboutImage>
              <motion.img
                src="https://images.unsplash.com/photo-1551434678-e076c223a692?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"
                alt="Seagull Technologies Team"
                initial={{ opacity: 0, scale: 0.9 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.7 }}
                viewport={{ once: true }}
              />
            </AboutImage>
          </AboutContent>
        </Container>
      </About>

      <Contact id="contact">
        <Container>
          <ContactContent>
            <ContactInfo>
              <motion.h2
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                viewport={{ once: true }}
              >
                Get in Touch
              </motion.h2>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.2 }}
                viewport={{ once: true }}
              >
                Have a project in mind or want to learn more about our AI app development services? We'd love to hear from you. Reach out to us using the contact form or our contact information below.
              </motion.p>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.3 }}
                viewport={{ once: true }}
              >
                <ContactItem>
                  <FaEnvelope />
                  <span>info@seagulltech.com</span>
                </ContactItem>
                <ContactItem>
                  <FaPhone />
                  <span>+1 (555) 123-4567</span>
                </ContactItem>
                <ContactItem>
                  <FaMapMarkerAlt />
                  <span>123 Innovation Street, Tech City, TC 12345</span>
                </ContactItem>
              </motion.div>
            </ContactInfo>
            <ContactForm onSubmit={handleSubmit}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.4 }}
                viewport={{ once: true }}
              >
                <FormGroup>
                  <Label htmlFor="name">Name</Label>
                  <Input type="text" id="name" required />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="email">Email</Label>
                  <Input type="email" id="email" required />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="subject">Subject</Label>
                  <Input type="text" id="subject" required />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="message">Message</Label>
                  <TextArea id="message" required></TextArea>
                </FormGroup>
                <SubmitButton type="submit">Send Message</SubmitButton>
              </motion.div>
            </ContactForm>
          </ContactContent>
        </Container>
      </Contact>
    </div>
  );
}

export default App;
