import React from 'react';
import styled from '@emotion/styled';
import { FaEnvelope, FaPhone, FaMapMarkerAlt } from 'react-icons/fa';

const HeaderContainer = styled.header`
  padding: 20px 0;
  position: fixed;
  width: 100%;
  top: 0;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(5px);
  z-index: 1000;
  border-bottom-left-radius: 15px;
  border-bottom-right-radius: 15px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
`;

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Logo = styled.h1`
  font-size: 1.5rem;
  color: #2d3436;
  margin: 0;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled.a`
  color: #2d3436;
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s ease;

  &:hover {
    color: #0984e3;
  }
`;

const Header = ({ scrollToSection }) => {
  return (
    <HeaderContainer>
      <Nav>
        <Logo>Seagull Technologies</Logo>
        <NavLinks>
          <NavLink href="#apps" onClick={(e) => scrollToSection(e, 'apps')}>Our Apps</NavLink>
          <NavLink href="#about" onClick={(e) => scrollToSection(e, 'about')}>About</NavLink>
          <NavLink href="#contact" onClick={(e) => scrollToSection(e, 'contact')}>Contact</NavLink>
        </NavLinks>
      </Nav>
    </HeaderContainer>
  );
};

export default Header;
