import React from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';

const AppCardContainer = styled(motion.div)`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  cursor: pointer;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
  }
`;

const AppLogo = styled.div`
  width: 120px;
  height: 120px;
  margin: 0 auto 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;

  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

const AppIcon = styled.div`
  font-size: 2.5rem;
  color: var(--primary-color);
  margin-bottom: 1rem;
  display: ${props => props.hide ? 'none' : 'block'};
`;

const AppTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: var(--text-color);
`;

const AppDescription = styled.p`
  color: var(--light-text);
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const AppLink = styled.a`
  display: inline-block;
  padding: 0.5rem 1rem;
  background: var(--primary-color);
  color: white;
  border-radius: 5px;
  font-weight: 500;
  transition: background 0.3s ease;

  &:hover {
    background: var(--secondary-color);
  }
`;

const AppCard = ({ app, index, openModal }) => {
  return (
    <AppCardContainer
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: index * 0.2 }}
      viewport={{ once: true }}
      onClick={() => app.url && window.open(app.url, '_blank')}
      style={{ opacity: app.id.includes('coming-soon') ? 0.7 : 1 }}
    >
      <AppLogo>
        <img 
          src={app.logo} 
          alt={`${app.title} Logo`} 
          onError={(e) => {
            e.target.onerror = null;
            e.target.style.display = 'none';
            e.target.parentElement.querySelector('.fallback-icon').style.display = 'block';
          }} 
        />
        <AppIcon className="fallback-icon" hide={app.logo !== '/images/logos/coming-soon-logo.png'}>
          {app.icon}
        </AppIcon>
      </AppLogo>
      <AppTitle>{app.title}</AppTitle>
      <AppDescription>
        {app.description}
      </AppDescription>
      <AppLink 
        href={app.url || "#"} 
        onClick={(e) => openModal(e, app)}
        target={app.url ? "_blank" : undefined}
        rel={app.url ? "noopener noreferrer" : undefined}
      >
        Learn More
      </AppLink>
    </AppCardContainer>
  );
};

export default AppCard;
