  };

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: ${props => props.isDarkMode ? 
      'rgba(45, 52, 54, 0.5)' : 
      'rgba(0, 0, 0, 0.1)'
    };
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb {
    background-color: var(--primary-color);
    border-radius: 4px;

    &:hover {
      background-color: var(--hover-color);
    }
  }
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: ${props => props.isDarkMode ? 
    'rgba(45, 52, 54, 0.8)' : 
    'rgba(255, 255, 255, 0.8)'
  };
  border: none;
  font-size: 1.5rem;
  color: var(--text-color);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  transition: all 0.3s ease;
  backdrop-filter: blur(5px);

  &:hover {
    background: ${props => props.isDarkMode ? 
      'rgba(45, 52, 54, 0.95)' : 
      'rgba(255, 255, 255, 0.95)'
    };
    color: var(--primary-color);
    transform: scale(1.1);
  }
`;

const ModalHeader = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 2rem;
  padding-right: 2rem;
`;

const ModalLogo = styled.div`
  width: 80px;
  height: 80px;
  margin-right: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${props => props.isDarkMode ? 
    'rgba(45, 52, 54, 0.5)' : 
    'rgba(255, 255, 255, 0.5)'
  };
  border-radius: 10px;
  padding: 0.5rem;

  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

const ModalTitle = styled.h2`
  font-size: 2rem;
  color: var(--text-color);
  margin: 0;
  font-weight: 600;
`;

const ModalBody = styled.div`
  margin-bottom: 2rem;
`;

const ModalText = styled.p`
  font-size: 1.1rem;
  line-height: 1.8;
  color: var(--light-text);
  margin-bottom: 2rem;
  opacity: ${props => props.isDarkMode ? '0.9' : '1'};
`;

const ModalFeatures = styled.div`
  background: ${props => props.isDarkMode ? 
    'rgba(45, 52, 54, 0.5)' : 
    'rgba(0, 0, 0, 0.03)'
  };
  padding: 1.5rem;
  border-radius: 10px;
  margin-bottom: 2rem;

  h3 {
    color: var(--text-color);
    margin-bottom: 1rem;
    font-size: 1.3rem;
  }
`;

const FeatureList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const FeatureItem = styled.li`
  display: flex;
  align-items: flex-start;
  margin-bottom: 0.75rem;
