const ModalFooter = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-top: 2rem;
  padding-top: 1rem;
  border-top: 1px solid var(--border-color);
`;

const ModalButton = styled.a`
  display: inline-block;
  padding: 0.75rem 1.5rem;
  background: var(--primary-color);
  color: white;
  border-radius: 5px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;

  &:hover {
    background: var(--hover-color);
    transform: translateY(-2px);
  }
`;

const AppIcon = styled.div`
  font-size: 2.5rem;
  color: var(--primary-color);
  margin-bottom: 1rem;
  display: ${props => props.hide ? 'none' : 'block'};
`;

const PrivacyPolicySection = styled.div`
  margin-top: 2.5rem;
  padding-top: 2rem;
  border-top: 1px solid var(--border-color);
  font-size: 0.95rem;
`;

const PrivacyPolicyTitle = styled.h3`
  font-size: 1.4rem;
  color: var(--text-color);
  margin-bottom: 1.5rem;
  font-weight: 600;
`;

const PrivacyPolicyText = styled.p`
  font-size: 0.95rem;
  line-height: 1.6;
  color: var(--light-text);
  margin-bottom: 1rem;
  opacity: ${props => props.isDarkMode ? '0.9' : '1'};

  &.intro {
    font-size: 1rem;
    margin-bottom: 2rem;
  }

  &:last-child {
    margin-bottom: 0;
  }
`;

const PrivacyPolicySectionTitle = styled.h4`
  font-size: 1.1rem;
  color: var(--text-color);
  margin: 1.75rem 0 0.75rem;
  font-weight: 600;
  display: flex;
  align-items: center;

  &:before {
    content: attr(data-number);
    font-size: 0.8rem;
    font-weight: 500;
    color: var(--primary-color);
    margin-right: 0.5rem;
    opacity: 0.9;
  }
`;

const BulletList = styled.ul`
  list-style-type: none;
  padding-left: 1rem;
  margin: 0.5rem 0 1rem;
`;

const BulletPoint = styled.li`
  display: flex;
  align-items: flex-start;
  margin-bottom: 0.5rem;
  color: var(--light-text);
  opacity: ${props => props.isDarkMode ? '0.9' : '1'};
  font-size: 0.95rem;
  line-height: 1.5;

  &:before {
    content: "•";
    color: var(--primary-color);
    font-weight: bold;
    margin-right: 0.5rem;
    opacity: 0.8;
  }

  &:last-child {
    margin-bottom: 0;
  }
`;

const BoldText = styled.span`
  font-weight: 600;
  color: var(--text-color);
  margin-right: 0.25rem;
`;

const AppModal = ({ isOpen, app, onClose }) => {
  const { isDarkMode } = useTheme();

  if (!app) return null;

  const renderPrivacyPolicy = () => {
    if (app.id !== 'dresscode') return null;

    return (
      <PrivacyPolicySection>
        <PrivacyPolicyTitle>Privacy Policy</PrivacyPolicyTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode} className="intro">
          Last Updated: March 15, 2025
        </PrivacyPolicyText>

        <PrivacyPolicyText isDarkMode={isDarkMode} className="intro">
          This Privacy Policy explains how Dresscode ("we", "us", or "our") collects, uses, and safeguards your information when you use our app. Dresscode allows you to upload outfit photos and receive AI-powered style rankings. All data you provide is stored securely in our database and is never shared with third parties.
        </PrivacyPolicyText>

        <PrivacyPolicySectionTitle data-number="1.">Information We Collect</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          We collect information to provide and improve the Service. This may include:
        </PrivacyPolicyText>

        <BulletList>
          <BulletPoint isDarkMode={isDarkMode}>
            <BoldText>Account Information:</BoldText> When you register, we collect your name, email address, and other credentials.
          </BulletPoint>
          <BulletPoint isDarkMode={isDarkMode}>
            <BoldText>Uploaded Content:</BoldText> Photos of your outfits and associated metadata.
          </BulletPoint>
          <BulletPoint isDarkMode={isDarkMode}>
            <BoldText>Usage Data:</BoldText> Information about how you use the Service, including interactions and app performance.
          </BulletPoint>
        </BulletList>

        <PrivacyPolicySectionTitle data-number="2.">How We Use Your Information</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          We use your information for:
        </PrivacyPolicyText>

        <BulletList>
          <BulletPoint isDarkMode={isDarkMode}>
            Providing personalized outfit rankings and recommendations using AI.
          </BulletPoint>
          <BulletPoint isDarkMode={isDarkMode}>
            Improving and optimizing the Service based on user behavior.
          </BulletPoint>
          <BulletPoint isDarkMode={isDarkMode}>
            Communicating updates, new features, and important information regarding your account.
          </BulletPoint>
        </BulletList>

        <PrivacyPolicySectionTitle data-number="3.">Data Security and Privacy</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          All images and personal information you provide are stored securely in our database. We design our system so that your data remains private—no one else has access to your content.
        </PrivacyPolicyText>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          We implement industry-standard security measures to protect your data from unauthorized access.
        </PrivacyPolicyText>

        <PrivacyPolicySectionTitle data-number="4.">Sharing and Disclosure</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          We do not sell or share your personal information or uploaded content with third parties. Your data is used solely to operate and improve the Service.
        </PrivacyPolicyText>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          We may share information if required by law, or if it is necessary to protect our rights or the safety of our users.
        </PrivacyPolicyText>

        <PrivacyPolicySectionTitle data-number="5.">Use of AI</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          Dresscode uses the OpenAI API to generate outfit rankings and style evaluations. These AI-driven insights are for your informational purposes only and do not affect your ownership of any content you upload.
        </PrivacyPolicyText>

        <PrivacyPolicySectionTitle data-number="6.">Data Retention</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          We retain your information as long as your account is active or as needed to provide the Service. You may request deletion of your account and its data at any time.
        </PrivacyPolicyText>

        <PrivacyPolicySectionTitle data-number="7.">Your Rights</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          Depending on your jurisdiction, you may have rights regarding your personal data, including the right to access, correct, or delete your information.
        </PrivacyPolicyText>

        <PrivacyPolicySectionTitle data-number="8.">Children's Privacy</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          Our Service is not intended for children under 13. We do not knowingly collect personal information from children under 13. If you believe we have inadvertently collected such data, please contact us.
        </PrivacyPolicyText>

        <PrivacyPolicySectionTitle data-number="9.">Changes to This Privacy Policy</PrivacyPolicySectionTitle>
        <PrivacyPolicyText isDarkMode={isDarkMode}>
          We may update this Privacy Policy from time to time. Significant changes will be communicated by updating the "Last Updated" date and posting a notice within the app.
        </PrivacyPolicyText>
      </PrivacyPolicySection>
    );
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <ModalOverlay
          isDarkMode={isDarkMode}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={onClose}
        >
          <ModalContent
            isDarkMode={isDarkMode}
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            transition={{ type: "spring", damping: 25, stiffness: 300 }}
            onClick={(e) => e.stopPropagation()}
          >
            <CloseButton onClick={onClose} isDarkMode={isDarkMode}>
              <FaTimes />
            </CloseButton>

            <ModalHeader>
              <ModalLogo isDarkMode={isDarkMode}>
                <img 
                  src={app.logo} 
                  alt={`${app.title} Logo`} 
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.style.display = 'none';
                    e.target.parentElement.querySelector('.fallback-icon').style.display = 'block';
                  }} 
                />
                <div className="fallback-icon" style={{ display: app.logo === '/images/logos/coming-soon-logo.png' ? 'block' : 'none' }}>
                  {app.icon}
                </div>
              </ModalLogo>
              <ModalTitle>{app.title}</ModalTitle>
            </ModalHeader>

            <ModalBody>
              <ModalText isDarkMode={isDarkMode}>{app.longDescription}</ModalText>

              <ModalFeatures isDarkMode={isDarkMode}>
                <h3>Key Features</h3>
                <FeatureList>
                  {app.features.map((feature, index) => (
                    <FeatureItem key={index} isDarkMode={isDarkMode}>{feature}</FeatureItem>
                  ))}
                </FeatureList>
              </ModalFeatures>

              {renderPrivacyPolicy()}
            </ModalBody>

            <ModalFooter>
              {!app.url && (
                <ModalButton href="#contact">
                  Get Notified
                </ModalButton>
              )}
            </ModalFooter>
          </ModalContent>
        </ModalOverlay>
      )}
    </AnimatePresence>
  );
};

export default AppModal;
